	end
end)

function DrawSub(msg, time)
  ClearPrints()
	SetTextEntry_2("STRING")
	AddTextComponentString(msg)
	DrawSubtitleTimed(time, 1)
end

function ShowLoadingPromt(msg, time, type)
	Citizen.CreateThread(function()
		Citizen.Wait(0)
		N_0xaba17d7ce615adbf("STRING")
		AddTextComponentString(msg)
		N_0xbd12f8228410d9b4(type)
		Citizen.Wait(time)
		N_0x10d373323e5b9c0d()
	end)
end

function GetRandomWalkingNPC()

	local search = {}
	local peds   = ESX.Game.GetPeds()

	for i=1, #peds, 1 do
		if IsPedHuman(peds[i]) and IsPedWalking(peds[i]) and not IsPedAPlayer(peds[i]) then
			table.insert(search, peds[i])
		end
	end

	if #search > 0 then
		return search[GetRandomIntInRange(1, #search)]
	end

	print('Using fallback code to find walking ped')

	while true do

		local ped = GetRandomPedAtCoord(0.0,  0.0,  0.0,  math.huge + 0.0,  math.huge + 0.0,  math.huge + 0.0,  26)

		if DoesEntityExist(ped) and IsPedHuman(ped) and IsPedWalking(ped) and not IsPedAPlayer(ped) then
			return ped
		end

	end

end

function ClearCurrentMission()

	if DoesBlipExist(CurrentCustomerBlip) then
		RemoveBlip(CurrentCustomerBlip)
	end

	if DoesBlipExist(DestinationBlip) then
		RemoveBlip(DestinationBlip)
	end

	CurrentCustomer           = nil
	CurrentCustomerBlip       = nil
	DestinationBlip           = nil
	IsNearCustomer            = false
	CustomerIsEnteringVehicle = false
	CustomerEnteredVehicle    = false
	TargetCoords              = nil

end

function StartAirlinesJob()

	ShowLoadingPromt(_U('taking_service') .. 'Airlines', 5000, 3)
	ClearCurrentMission()

	OnJob = true

end

function StopAirlinesJob()

	local playerPed = GetPlayerPed(-1)

	if IsPedInAnyVehicle(playerPed, false) and CurrentCustomer ~= nil then
		local vehicle = GetVehiclePedIsIn(playerPed,  false)
		TaskLeaveVehicle(CurrentCustomer,  vehicle,  0)

		if CustomerEnteredVehicle then
			TaskGoStraightToCoord(CurrentCustomer,  TargetCoords.x,  TargetCoords.y,  TargetCoords.z,  1.0,  -1,  0.0,  0.0)
		end

	end

 	ClearCurrentMission()

	OnJob = false

	DrawSub(_U('mission_complete'), 5000)

end

function OpenAirlinesActionsMenu()

	local elements = {
		{label = _U('spawn_veh'), value = 'spawn_vehicle'},
		{label = _U('deposit_stock'), value = 'put_stock'},
 		{label = _U('take_stock'), value = 'get_stock'}
	}

	if Config.EnablePlayerManagement and PlayerData.job ~= nil and PlayerData.job.grade_name == 'boss' then
  	table.insert(elements, {label = _U('boss_actions'), value = 'boss_actions'})
	end

	ESX.UI.Menu.CloseAll()

	ESX.UI.Menu.Open(
		'default', GetCurrentResourceName(), 'airlines_actions',
		{
			title    = 'Airlines',
			elements = elements
		},
		function(data, menu)

			if data.current.value == 'put_stock' then
		    OpenPutStocksMenu()
	    end	

	    if data.current.value == 'get_stock' then
		    OpenGetStocksMenu()
	    end

			if data.current.value == 'spawn_vehicle' then

				if Config.EnableSocietyOwnedVehicles then

					local elements = {}

					ESX.TriggerServerCallback('esx_society:getVehiclesInGarage', function(vehicles)

						for i=1, #vehicles, 1 do
							table.insert(elements, {label = GetDisplayNameFromVehicleModel(vehicles[i].model) .. ' [' .. vehicles[i].plate .. ']', value = vehicles[i]})
						end

						ESX.UI.Menu.Open(
							'default', GetCurrentResourceName(), 'vehicle_spawner',
							{
								title    = _U('spawn_veh'),
								align    = 'top-left',
								elements = elements,
							},
							function(data, menu)

								menu.close()

								local vehicleProps = data.current.value

								ESX.Game.SpawnVehicle(vehicleProps.model, Config.Zones.VehicleSpawnPoint.Pos, 270.0, function(vehicle)
									ESX.Game.SetVehicleProperties(vehicle, vehicleProps)
									local playerPed = GetPlayerPed(-1)
									TaskWarpPedIntoVehicle(playerPed,  vehicle,  -1)
								end)

								TriggerServerEvent('esx_society:removeVehicleFromGarage', 'luxor2', vehicleProps)

							end,
							function(data, menu)
								menu.close()
							end
						)

					end, 'luxor2')

				else

					menu.close()

					if Config.MaxInService == -1 then

						local playerPed = GetPlayerPed(-1)
						local coords    = Config.Zones.VehicleSpawnPoint.Pos

						ESX.Game.SpawnVehicle('luxor2', coords, 225.0, function(vehicle)
							TaskWarpPedIntoVehicle(playerPed,  vehicle, -1)
						end)

					else

						ESX.TriggerServerCallback('esx_service:enableService', function(canTakeService, maxInService, inServiceCount)

							if canTakeService then

								local playerPed = GetPlayerPed(-1)
								local coords    = Config.Zones.VehicleSpawnPoint.Pos

								ESX.Game.SpawnVehicle('luxor2', coords, 225.0, function(vehicle)
									TaskWarpPedIntoVehicle(playerPed,  vehicle, -1)
								end)

							else

								ESX.ShowNotification(_U('full_service') .. inServiceCount .. '/' .. maxInService)

							end

						end, 'luxor2')

					end

				end

			end

			if data.current.value == 'boss_actions' then
				TriggerEvent('esx_society:openBossMenu', 'airlines', function(data, menu)
					menu.close()
				end)
			end

		end,
		function(data, menu)

			menu.close()

			CurrentAction     = 'airlines_actions_menu'
			CurrentActionMsg  = _U('press_to_open')
			CurrentActionData = {}

		end
	)

end

function OpenMobileAirlinesActionsMenu()

	ESX.UI.Menu.CloseAll()

	ESX.UI.Menu.Open(
		'default', GetCurrentResourceName(), 'mobile_airlines_actions',
		{
			title    = 'Airlines',
			elements = {
				{label = _U('billing'), value = 'billing'}
			}
		},
		function(data, menu)

			if data.current.value == 'billing' then

				ESX.UI.Menu.Open(
					'dialog', GetCurrentResourceName(), 'billing',
					{
						title = _U('invoice_amount')
					},
					function(data, menu)

						local amount = tonumber(data.value)

						if amount == nil then
							ESX.ShowNotification(_U('amount_invalid'))
						else

							menu.close()

							local closestPlayer, closestDistance = ESX.Game.GetClosestPlayer()

							if closestPlayer == -1 or closestDistance > 3.0 then
								ESX.ShowNotification(_U('no_players_near'))
							else
								TriggerServerEvent('esx_billing:sendBill', GetPlayerServerId(closestPlayer), 'society_airlines', 'Airlines', amount)
							end

						end

					end,
					function(data, menu)
						menu.close()
					end
				)

			end

		end,
		function(data, menu)
			menu.close()
		end
	)

end

